function MsgNotify( cMsg, cType ) { 
	cType =  ( typeof cType == 'undefined' ? 'success' : cType )
	$.notify( cMsg, { className: cType })
}

function NotifyInfo( cMsg ) { $.notify( cMsg, { className: 'info' } ) }
function NotifySuccess( cMsg ) { $.notify( cMsg, { className: 'success' }) }
function NotifyError( cMsg ) { $.notify( cMsg, { className: 'error' }) }
function NotifyWarn( cMsg ) { $.notify( cMsg, { className: 'warn' }) }
